# remove NA value
bloodtest <- na.omit(bloodtest)   

# change column names
colnames(bloodtest) <- c("FG","TG","TC","HDL-C","LDL-C", "SBP","DBP")

# check classification of variable  
str(bloodtest)

# switch FG to numeric variable
bloodtest$FG <- as.numeric(bloodtest$FG)

# switch tibble into dataframe
bloodtest <- as.data.frame(bloodtest)

# check classification again
str(bloodtest)

# View bloodtest
View(bloodtest)

# publication ready summary of bloodtest dataframe 
tbl_summary(bloodtest)

# build model
model_lm <- glm(FG~., data = bloodtest)

# accessing coefficient
tidy(model_lm)

# accessing coefficient
confint(model_lm)

# check assumption using plot
plot(model_lm)

# publication ready summary of regression model
model_lm %>%
  tbl_regression(exponentiate = TRUE) %>%
  bold_labels()
